import React from "react";
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';



class Login extends React.Component{

 state = {

    }
    handleChange = (e)=>{
        this.setState({
            [e.target.id]:e.target.value
        })
    }

    handleSubmit=(e)=>{
        e.preventDefault();
        console.log(this.state)
    }
    
    render(){
        return(

            <div className='App-container'style={{backgroundColor:'#121212',fontWeight:"bold"}}>
                <form onSubmit={this.handleSubmit} autoComplete='off' >
                    <h5 className='gray-text text-draken-3'> </h5>
                    <div className='input-field'>
                        <label htmlFor='email' >Email</label>
                        <input className='input-data' type='email' id='email'  required  onChange={this.handleChange}/>
                    </div>
                    <div className='input-field'>
                        <label htmlFor='password'>password</label>
                        <input className='input-data' type='password' id='password' required data-length="10" onChange={this.handleChange}/>
                    </div>
                </form>
                <div style={{textAlign:"center"}}>
                        <Link href='Home' className="btn btn-primary" style={{color:'white',width:'50%'}}>Sign up</Link>
                    </div>

                <Box mt={5} >
                    <Typography style={{color:'white'}} variant="body2" color="textSecondary" align="center">
                        {'Copyright © '}
                        <Link color="blue" href="https://libraryforall.org/">
                            Library for all
                        </Link>
                        {' '}{new Date().getFullYear()}{'.'}
                    </Typography>
                </Box>
            </div>      

            
  
        )
    }
}

export default Login
