import React, { useState } from 'react';
import Typography from '@material-ui/core/Typography';
import { useForm } from "react-hook-form";
import {Button} from 'react-bootstrap'
export default function Translate() {


    const { register, handleSubmit } = useForm();

    const onSubmit = data => {
        console.log(data);
    };

    return (
        <div>
            <div style={topcontainer}>
                <Typography component="h1" variant="h2" align="center" style={{ color: 'white' }} >
                    Book available
                </Typography>
                <Typography variant="h5" align="center" style={subheadding} >
                    In this page, you can select a book to translate
                </Typography>
                <div >
                <form onSubmit={handleSubmit(onSubmit)} style={{display:'inline-block'}}>
                    <input style={input_field} 
                        name="title" 
                        ref={register}
                        placeholder="type Title"/>    

                    <input style={input_field}
                        name="Level"
                        ref={register}
                        placeholder="type Level"
                    />
                    
                    
                </form>
            </div>
            </div>

        </div>
    )
}




const topcontainer = {
    backgroundColor: '#273746',
    paddingTop: '100px',
    paddingBottom: '50px',
    paddingLeft: '300px',
    paddingRight: '300px',

}

const subheadding = {
    color: 'white',
    marginTop: '50px',
    marginLeft: '200px',
    marginRight: '200px'
}

const input_field={
    width:'200px',
    marginLeft:'100px'
}
