import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import { Button } from "@material-ui/core";
import Icon from '@material-ui/core/Icon';
import { Input } from '@material-ui/core';
import { Link } from 'react-router-dom'
import {Form} from 'react-bootstrap'

function Searchbar(props) {

    const [state, setState] = useState({
        bookname: '',
        level: ''
    })
    const handlesubmit = (e) => {
        e.preventDefault();
        console.log(state)
    }


    return (
        <div>
<Form>
        <Form.Group widths='equal'>
          <Form.Field
            control={Input}
            label='First name'
            placeholder='First name'
          />
          <Form.Field
            control={Input}
            label='Last name'
            placeholder='Last name'
          />
          <Form.Field
            control={Select}
            label='Gender'
            options={options}
            placeholder='Gender'
          />
        </Form.Group>
        <Form.Group inline>
          <label>Quantity</label>
          <Form.Field
            control={Radio}
            label='One'
            value='1'
            checked={value === '1'}
            onChange={this.handleChange}
          />
          <Form.Field
            control={Radio}
            label='Two'
            value='2'
            checked={value === '2'}
            onChange={this.handleChange}
          />
          <Form.Field
            control={Radio}
            label='Three'
            value='3'
            checked={value === '3'}
            onChange={this.handleChange}
          />
        </Form.Group>
        <Form.Field
          control={TextArea}
          label='About'
          placeholder='Tell us more about you...'
        />
        <Form.Field
          control={Checkbox}
          label='I agree to the Terms and Conditions'
        />
        <Form.Field control={Button}>Submit</Form.Field>
      </Form>

        </div>
    )
}
export default function Translate() {
    const classes = useStyles();
    const [search, setSearch] = useState()
    return (
        <div>
            <div style={topcontainer}>
                <Typography component="h1" variant="h2" align="center" style={{ color: 'white' }} >
                    Book available
    </Typography>
                <Typography variant="h5" align="center" style={subheadding} >
                    In this page, you can select a book to translate
    </Typography>
            </div>
            <div className='Seachbar'>
                <Searchbar onSubmit={newSearch => setSearch(newSearch)} />
            </div>
        </div>
    )
}


const useStyles = makeStyles((theme) => ({
    root: {
        clear: 'both',
        display: 'flex',
        flexWrap: 'wrap',
        justify: 'flex-start',
        overflow: 'hidden',
    },
    search: {
        border: '10px',
        borderColor: 'blue'
    },


    gridList: {
        width: '100%',
        height: 750,
    },
    icon: {
        color: 'rgba(255, 255, 255, 0.54)',
    },
}));

const topcontainer = {
    backgroundColor: '#273746',
    paddingTop: '100px',
    paddingBottom: '50px',
    paddingLeft: '300px',
    paddingRight: '300px',

}

const subheadding = {
    color: 'white',
    marginTop: '50px',
    marginLeft: '200px',
    marginRight: '200px'
}
