import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import ListSubheader from '@material-ui/core/ListSubheader';
import IconButton from '@material-ui/core/IconButton';
import InfoIcon from '@material-ui/icons/Info';
import title1 from './How The Rooster Found His Sound/552 English/552 How The Rooster Found His Sound_reflow/OEBPS/Images/cover.jpg';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    overflow: 'hidden',
    backgroundColor: theme.palette.background.paper,
  },
  gridList: {
    width: 500,
    height: 750,
  },
  icon: {
    color: 'rgba(255, 255, 255, 0.54)',
  },
}));



 


const info = [
 {
      img: title1,
      title: 'How The Rooster Found His Sound',
     author: 'abc',
   },

  ];

export default function TitlebarGridList() {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <GridList cellHeight={300} className={classes.gridList}>

        {info.map((info) => (
          <GridListTile key={info.img}>
            <img src={info.img} alt={info.title} />
            <GridListTileBar
              title={info.title}
              subtitle={<span>by: {info.author}</span>}
              actionIcon={
                <IconButton aria-label={`info about ${info.title}`} className={classes.icon}>
                  <InfoIcon />
                </IconButton>
              }
            />
          </GridListTile>
        ))}
      </GridList>
    </div>
  );
}
