import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import Icon from '@material-ui/core/Icon';
import { Link } from 'react-router-dom'

import {
    Button,
    Checkbox,
    Form,
    Input,
    Radio,
    Select,
    TextArea,
  } from 'semantic-ui-react'
  
  const options = [
    { key: 'm', text: 'Male', value: 'male' },
    { key: 'f', text: 'Female', value: 'female' },
    { key: 'o', text: 'Other', value: 'other' },
  ]

function Searchbar(props) {

    const [state, setState] = useState({
        bookname: '',
        level: ''
    })
    const handlesubmit = (e) => {
        e.preventDefault();
        console.log(state)
    }


    return (
        <div>
<Form>
        <Form.Group widths='equal'>
          <Form.Field
            control={Input}
            label='First name'
            placeholder='First name'
          />
          <Form.Field
            control={Input}
            label='Last name'
            placeholder='Last name'
          />
          <Form.Field
            control={Select}
            label='Gender'
            options={options}
            placeholder='Gender'
          />
        </Form.Group>
        <Form.Group inline>
          <label>Quantity</label>
          <Form.Field
            control={Radio}
            label='One'
            value='1'
            checked={value === '1'}
            onChange={this.handleChange}
          />
          <Form.Field
            control={Radio}
            label='Two'
            value='2'
            checked={value === '2'}
            onChange={this.handleChange}
          />
          <Form.Field
            control={Radio}
            label='Three'
            value='3'
            checked={value === '3'}
            onChange={this.handleChange}
          />
        </Form.Group>
        <Form.Field
          control={TextArea}
          label='About'
          placeholder='Tell us more about you...'
        />
        <Form.Field
          control={Checkbox}
          label='I agree to the Terms and Conditions'
        />
        <Form.Field control={Button}>Submit</Form.Field>
      </Form>

        </div>
    )
}
export default function Translate() {
    const classes = useStyles();
    const [search, setSearch] = useState()
    return (
        <div>
            <div style={topcontainer}>
                <Typography component="h1" variant="h2" align="center" style={{ color: 'white' }} >
                    Book available
    </Typography>
                <Typography variant="h5" align="center" style={subheadding} >
                    In this page, you can select a book to translate
    </Typography>
            </div>
            <div className='Seachbar'>
                <Searchbar onSubmit={newSearch => setSearch(newSearch)} />
            </div>
        </div>
    )
}


const useStyles = makeStyles((theme) => ({
    root: {
        clear: 'both',
        display: 'flex',
        flexWrap: 'wrap',
        justify: 'flex-start',
        overflow: 'hidden',
    },
    search: {
        border: '10px',
        borderColor: 'blue'
    },


