import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import Typography from '@material-ui/core/Typography';
import { Button } from "@material-ui/core";
import Icon from '@material-ui/core/Icon';
import { Input } from '@material-ui/core';
import { Link } from 'react-router-dom'


function Searchbar(props) {
    const [bookname, setBookname] = useState("");
    const [level, setLevel]=useState()

    return (
        <div>
            <form style={{display:'flex',justifyContent:'space-between'}}>
                <Input

                    style={{ width: '80%', color: 'black', fontWeight: "bold" }}
                    placeholder="Search some book here..."
                    value={bookname}
                    onChange={e => {
                        setBookname(e.target.value);
                    }}

                />

                <Input

                    style={{ width: '80%', color: 'black', fontWeight: "bold" }}
                    placeholder="Search some book here..."
                    value={search}
                    onChange={e => {
                        setSearch(e.target.value);
                    }}
                    onKeyPress={e => {
                        if (e.key === "Enter") {
                            if (props.onSubmit) {
                                props.onSubmit(search);
                            }
                        }
                    }}
                />
            </form>
            <span style={{ position: "relative" }}><Button variant="contained" width='15%' float='right'
                onClick={e => {
                    if (props.onSubmit) {
                        props.onSubmit(search);
                    }
                }}
            >

                Search
        </Button>
            </span>
        </div>
    )
}
export default function Translate() {
    const classes = useStyles();
    const [search, setSearch] = useState()
    return (
        <div>
            <div style={topcontainer}>
                <Typography component="h1" variant="h2" align="center" style={{ color: 'white' }} >
                    Book available
    </Typography>
                <Typography variant="h5" align="center" style={subheadding} >
                    In this page, you can select a book to translate
    </Typography>
            </div>
            <div className='Seachbar'>
                <Searchbar onSubmit={newSearch => setSearch(newSearch)} />
            </div>
        </div>
    )
}


const useStyles = makeStyles((theme) => ({
    root: {
        clear: 'both',
        display: 'flex',
        flexWrap: 'wrap',
        justify: 'flex-start',
        overflow: 'hidden',
    },
    search: {
        border: '10px',
        borderColor: 'blue'
    },


    gridList: {
        width: '100%',
        height: 750,
    },
    icon: {
        color: 'rgba(255, 255, 255, 0.54)',
    },
}));

const topcontainer = {
    backgroundColor: '#273746',
    paddingTop: '100px',
    paddingBottom: '50px',
    paddingLeft: '300px',
    paddingRight: '300px',

}

const subheadding = {
    color: 'white',
    marginTop: '50px',
    marginLeft: '200px',
    marginRight: '200px'
}
